import React, { useEffect, useRef } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { getProductsThunk } from '../../store/products';
import ProductCard from '../ProductCard/ProductCard';

const HomePageProducts = () => {
  const dispatch = useDispatch();
  const productObj = useSelector((state) => state.productState);
  const productArr = Object.values(productObj);
  const containerRef = useRef(null);

  useEffect(() => {
    dispatch(getProductsThunk());
  }, [dispatch]);

  const scrollLeft = () => {
    containerRef.current.scrollLeft -= 500; // Adjust the scroll distance as per your requirement
  };

  const scrollRight = () => {
    containerRef.current.scrollLeft += 500; // Adjust the scroll distance as per your requirement
  };

  return (
    <div className='HomePageProductsContainer' style={{ width: '1200px', overflowX: 'auto' }}>
      <div className='HomePageEachProduct' ref={containerRef}>
        {productArr.map((product) => (
          <ProductCard key={product?.id} product={product} />
        ))}
      </div>
      <button onClick={scrollLeft}>Left</button>
      <button onClick={scrollRight}>Right</button>
    </div>
  );
};

export default HomePageProducts;
