import React, { useState, useEffect } from "react";
import { useDispatch } from "react-redux";
import { Route, Switch } from "react-router-dom";
import * as sessionActions from "./store/session";
import { useHistory } from "react-router-dom";


function App() {

  const dispatch = useDispatch();
  const [isLoaded, setIsLoaded] = useState(false);
  useEffect(() => {
    dispatch(sessionActions.restoreUser()).then(() => setIsLoaded(true));
  }, [dispatch]);

  // const history = useHistory()
  // const redirectToSongs = () => {
  //   history.push('/products')
  // }

  return (
    <>
    <div>
      <Navigation isLoaded={isLoaded} />
      {isLoaded && (
        <Switch>
          <Route exact path="/">

      <div className="imageContainer">
      <img className = "homeimage" src="https://images.unsplash.com/photo-1533174072545-7a4b6ad7a6c3?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=1740&q=80"
     alt = "homepage"  />
     <div className="imageText" > Discover more with SoundCloud </div>
      <button className="getstartedbtn" onClick={redirectToSongs}>  get started</button>
      </div>

          </Route>
          <Route path="/login">
            <LoginFormPage />
          </Route>
          <Route path="/signup">
            <SignupFormPage />
          </Route>
          <Route exact path="/songs">
            <SongBrowser />
          </Route>
          <Route path="/songs/new">
            <CreateSongForm />
          </Route>
          <Route path="/songs/:songId/edit">
            <EditSongForm />
          </Route>
          <Route exact path="/songs/:songId">
            <SongDetail />
          </Route>
        </Switch>
      )}
      </div>

    </>
  );
}

export default App;
