import './ReviewCard.css'
import { useSelector, useDispatch } from 'react-redux';
import { deleteReviewThunk } from '../../store/reviews';
import {  useHistory } from 'react-router-dom';
import { useEffect, useState } from 'react';
import { FaStar } from "react-icons/fa";



const ReviewCard = ({review}) => {

  const dispatch = useDispatch()
  const [rating, setRating] = useState(Math.floor(review?.rating))
  const [hover, setHover] = useState(0)
  const history = useHistory()
  const sessionUser = useSelector(state => state.session.user)
  const isOwner =sessionUser && sessionUser.user.id === review.userId

  const deleteHandler = () => {
    dispatch(deleteReviewThunk(review.id))
    history.push(`/products/{}`)
    }


    return (
        <>
{/* <div className = "ReviewInput">{Math.floor(review?.rating)}</div> */}

<div className="star-rating">
      {[...Array(5)].map((star, index) => {
        const ratingValue = index + 1;
        return (
            <label>
            <h
            className='star-rating'
            name='rating'
            value={ratingValue}
                        />
            <FaStar
            className='star'
            color= {ratingValue <= (rating)? "#ffc107" : "#e4e5e9"}
            size={25}
            />

            </label>

        );
      })}

    </div>
        <div className="reviewCard">
            <div className='reviewCard'>
            {/* <div className = "reviewUser">{review.User.username}</div> */}
            <div className = "ReviewInput">{review?.body}</div>
            {isOwner && <button className="deleteReviewBtn" onClick={deleteHandler}> Delete Review</button>}
            </div>
        </div>
        </>
    )
}

export default ReviewCard
