import React from 'react';
import './ProductCard.css';
import { Link } from 'react-router-dom';
import { useSelector } from 'react-redux';
import { useHistory } from "react-router-dom";

const ProductCard = ({ product }) => {
  const sessionUser = useSelector((state) => state.session.user);
  const history = useHistory();
  const isOwner = sessionUser && sessionUser.user; // Check if sessionUser exists and has a user property

  const handleClick = () => {
    if (isOwner) {
      history.push(`/products/${product.id}`);
    } else {
      alert('Please log in to view details'); // Display a pop-up message
    }
  };

  return (
    <div className='productCardContainer'>
      {isOwner ? (

          <img className='productimg' onClick={handleClick} src={product.previewImageUrl} alt='productimg' />

      ) : (
        <div className='productImgTag'>
          <img className='productimg' src={product.previewImageUrl} alt='productimg' />
        </div>
      )}

      {isOwner ? (
        <Link className='titlelink' key={product?.id} to={`/products/${product.id}`}>
          {product.name}
        </Link>
      ) : (
        <div className='titlelink' onClick={handleClick}>
          {product.name}
        </div>
      )}
    </div>
  );
};

export default ProductCard;
