import './Cart.css';
import {
  loadAllCartThunk,
  editQuantityThunk,
  deleteCartThunk,
  clearCart,
} from '../../store/cart';
import { useSelector, useDispatch } from 'react-redux';
import { useEffect, useState } from 'react';
import { useHistory } from 'react-router-dom';

const Cart = () => {
  const dispatch = useDispatch();
  const [loaded, setLoaded] = useState(false);
  const cartItemsObj = useSelector((state) => state.cartState.cartItems);
  const history = useHistory();
  const cartId = useSelector(
    (state) => state.session.user.currentCart.id
  );

  const cartItemsArr = Object.values(cartItemsObj);

  useEffect(() => {
    dispatch(loadAllCartThunk({ cartId })).then(() => setLoaded(true));
  }, [dispatch]);

  const handleQuantityChange = (
    cartItemId,
    newQuantity,
    productId,
    cartId
  ) => {
    if (newQuantity < 1) {
      handleRemoveFromCart(cartId, productId);
    } else {
      dispatch(
        editQuantityThunk(cartItemId, newQuantity, productId, cartId)
      );
    }
  };

  const handleRemoveFromCart = (cartId, productId) => {
    dispatch(deleteCartThunk(cartId, productId));
  };

  const handleCheckout = () => {
    history.push('/checkedOut');
  };

  const handleContinueShopping = () => {
    history.push('/products');
  };

  const handleClearCart = (cartId) => {
    dispatch(clearCart(cartId));
  };

  const getTotalPriceAndCount = () => {
    let totalPrice = 0;
    let itemCount = 0;

    cartItemsArr.forEach((product) => {
      totalPrice += product.Product.price * product.quantity;
      itemCount += product.quantity;
    });

    return {
      totalPrice: totalPrice.toFixed(2),
      itemCount: itemCount,
    };
  };

  return loaded && (
    <>
      <div className="shoppingCartBanner"> SHOPPING BAG </div>
      <div className="Cart">
        {cartItemsArr.length === 0 ? (
          <div className="EmptyCartMessage">Shopping cart is empty</div>
        ) : (
          cartItemsArr.map((product) => (
            <div key={product.Product.name} className="CartItem">
              <div className="CartItem__Image">
                <img
                  src={product?.Product.previewImageUrl}
                  alt={product?.Product.name}
                />
              </div>
              <h3 className="CartItem__Name">
                <a href={`/products/${product.Product.id}`}>
                  {product?.Product.name}
                </a>
              </h3>
              {/* Rest of the component code */}
            </div>
          ))
        )}

        <div className="orderSummary">
          <h1>Order Summary</h1>
          <h3 className="ItemCount">
            {getTotalPriceAndCount().itemCount} Items
          </h3>
          {/* Rest of the component code */}
        </div>

        <div className="Cart__Buttons">
          {/* Rest of the component code */}
        </div>

        <button
          className="CheckoutButton"
          onClick={() => handleClearCart(cartId)}
        >
          clear cart
        </button>
      </div>
    </>
  );
};

export default Cart;
