import './Cart.css'
import {loadAllCartThunk} from '../../store/cart'
import {getProductsThunk } from '../../store/products';
import { useSelector, useDispatch } from 'react-redux';
import { useState, useEffect } from 'react';


const Cart = ( ) => {


    const dispatch = useDispatch()
    const productObj = useSelector(state => state.productState)
    const productArr = Object.values(productObj)

    const cartItemsArr = useSelector(state => {
        return state.cartState.cartItems
      });

    useEffect(() => {
        dispatch(loadAllCartThunk())
      }, [dispatch])

      useEffect(() => {
        dispatch(getProductsThunk())
      }, [dispatch])


      const cartItems = cartItemsArr.map((cartItem) => {
        const products = productArr.find((product) => product.id === cartItem.productId);
        return {
          name: products.name,
          price: products.price,
          previewImage: products.previewImageUrl,
          quantity: cartItem.quantity
        };
      });

      console.log(cartItems);

      return (
        <div className="CartItemList">
          {cartItems.map((item, index) => (
            <div className="CartItem" key={index}>
              <div className="CartItemPreview">
                <img src={item.previewImage} alt="Product Preview" />
              </div>
              <div className="CartItemDetails">
                <h3>{item.name}</h3>
                <div className="CartItemPrice">
                  <p>Price: ${item.price}</p>
                </div>
                <div className="CartItemQuantity">
                  <p>Quantity: {item.quantity}</p>
                </div>
              </div>
            </div>
          ))}
        </div>
      );

}


export default Cart
