import './ProductList.css';
import { useEffect, useState, useRef } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { getProductsThunk } from '../../store/products';
import ProductCard from '../ProductCard/ProductCard';

const ProductList = () => {
  const [activeFilter, setActiveFilter] = useState('all');
  const [activeWomenCategory, setActiveWomenCategory] = useState('all');
  const [activeMenCategory, setActiveMenCategory] = useState('all');
  const [isHoveringWomen, setIsHoveringWomen] = useState(false);
  const [isHoveringMen, setIsHoveringMen] = useState(false);
  const dispatch = useDispatch();
  const womenRef = useRef(null);
  const menRef = useRef(null);

  const productObj = useSelector((state) => state.productState);
  const productArr = Object.values(productObj);

  const filteredProducts = productArr.filter((product) => {
    if (activeFilter === 'all') {
      return true;
    } else if (activeFilter === 'women') {
      if (activeWomenCategory === 'all') {
        return product.category.toLowerCase() === 'women';
      } else {
        return (
          product.category.toLowerCase() === 'women' &&
          product.type.toLowerCase() === activeWomenCategory
        );
      }
    } else if (activeFilter === 'men') {
      if (activeMenCategory === 'all') {
        return product.category.toLowerCase() === 'men';
      } else {
        return (
          product.category.toLowerCase() === 'men' &&
          product.type.toLowerCase() === activeMenCategory
        );
      }
    }
    return false;
  });

  const handleFilterChange = (filter) => {
    setActiveFilter(filter);
  };

  const handleWomenCategoryChange = (category) => {
    setActiveWomenCategory(category);
  };

  const handleMenCategoryChange = (category) => {
    setActiveMenCategory(category);
  };

  const handleWomenHover = (isHovering) => {
    setIsHoveringWomen(isHovering);
  };

  const handleMenHover = (isHovering) => {
    setIsHoveringMen(isHovering);
  };

  const handleWomenSubFilterClick = (category) => {
    setActiveWomenCategory(category);
    womenRef.current.focus();
  };

  const handleMenSubFilterClick = (category) => {
    setActiveMenCategory(category);
    menRef.current.focus();
  };

  useEffect(() => {
    dispatch(getProductsThunk());
  }, [dispatch]);

  return (
    <main className='productListContainer'>
      <div className='filterButtonsContainer'>
        <button
          className='filterButtons'
          onClick={() => handleFilterChange('all')}
          onMouseEnter={() => handleWomenHover(false)}
          onMouseLeave={() => handleWomenHover(false)}
        >
          All Products
        </button>
        <button
          ref={womenRef}
          className='filterButtons'
          onClick={() => handleFilterChange('women')}
          onMouseEnter={() => handleWomenHover(true)}
          onMouseLeave={() => handleWomenHover(false)}
        >
          Women
        </button>
        <button
          ref={menRef}
          className='filterButtons'
          onClick={() => handleFilterChange('men')}
          onMouseEnter={() => handleMenHover(true)}
          onMouseLeave={() => handleMenHover(false)}
        >
          Men
        </button>
      </div>
      {(isHoveringWomen || activeFilter === 'women') && (
        <div className='subFilters' onMouseEnter={() => handleWomenHover(true)} onMouseLeave={() => handleWomenHover(false)}>
          <button onClick={() => handleWomenSubFilterClick('jackets')}>Jackets</button>
          <button onClick={() => handleWomenSubFilterClick('pants')}>Pants</button>
          <button onClick={() => handleWomenSubFilterClick('goggles')}>Goggles</button>
          <button onClick={() => handleWomenSubFilterClick('fleece')}>Fleece</button>
          <button onClick={() => handleWomenSubFilterClick('snowboards')}>Snowboards</button>
          <button onClick={() => handleWomenSubFilterClick('skis')}>Skis</button>
          <button onClick={() => handleWomenSubFilterClick('boots')}>Boots</button>
          <button onClick={() => handleWomenSubFilterClick('other')}>Other</button>
        </div>
      )}
      {(isHoveringMen || activeFilter === 'men') && (
        <div className='subFilters' onMouseEnter={() => handleMenHover(true)} onMouseLeave={() => handleMenHover(false)}>
          <button onClick={() => handleMenSubFilterClick('jackets')}>Jackets</button>
          <button onClick={() => handleMenSubFilterClick('pants')}>Pants</button>
          <button onClick={() => handleMenSubFilterClick('goggles')}>Goggles</button>
          <button onClick={() => handleMenSubFilterClick('fleece')}>Fleece</button>
          <button onClick={() => handleMenSubFilterClick('snowboards')}>Snowboards</button>
          <button onClick={() => handleMenSubFilterClick('skis')}>Skis</button>
          <button onClick={() => handleMenSubFilterClick('boots')}>Boots</button>
          <button onClick={() => handleMenSubFilterClick('other')}>Other</button>
        </div>
      )}

      <div className='eachProduct'>
        {filteredProducts.map((product) => (
          <ProductCard key={product?.id} product={product} />
        ))}
      </div>
    </main>
  );
};

export default ProductList;
