import './Cart.css'
import {loadAllCartThunk, editQuantity, clearCart } from '../../store/cart'
import {getProductsThunk } from '../../store/products';
import { useSelector, useDispatch } from 'react-redux';
import { useState, useEffect } from 'react';

const Cart = () => {
    const dispatch = useDispatch();
    const productObj = useSelector((state) => state.productState);
    const productArr = Object.values(productObj);

    const cartItemsArr = useSelector((state) => {
      return state.cartState.cartItems;
    });

    useEffect(() => {
      dispatch(loadAllCartThunk());
      dispatch(getProductsThunk());
    }, [dispatch]);

    const handleQuantityChange = (cartItemId, newQuantity) => {
        if (newQuantity <= 0) {
          // Clear the cart item if the quantity becomes zero or negative
          dispatch(clearCart());
        } else {
          // Dispatch the editQuantity action to update the quantity
          dispatch(editQuantity(cartItemId, newQuantity));
        }
      };

    const cartItems = cartItemsArr.map((cartItem) => {
      const product = productArr.find((product) => product.id === cartItem.productId);
      const total = product.price * cartItem.quantity;
      return {
        name: product.name,
        price: product.price,
        previewImage: product.previewImageUrl,
        quantity: cartItem.quantity,
        total,
      };
    });

    return (
      <div className="CartItemList">
        {cartItems.map((item, index) => (
          <div className="CartItem" key={index}>
            <div className="CartItemPreview">
              <img src={item.previewImage} alt={item.name} />
            </div>
            <div className="CartItemDetails">
              <div className="CartItemName">{item.name}</div>
              <div>
              <span className="CartItemQuantity">Quantity: {item.quantity}</span>
                <span className="CartItemPrice">${item.total}</span>

              </div>
            </div>
          </div>
        ))}
      </div>
    );
  };

  export default Cart;
