import './CreateReviewForm.css'
import { useSelector, useDispatch} from 'react-redux'
import { useState } from 'react';
import { useHistory, useParams } from 'react-router-dom';
import { createReviewThunk } from '../../store/reviews';

const CreateReviewForm =() => {

    const dispatch = useDispatch()
    const sessionUser = useSelector(state => state.session.user);

    const productIdobj = useParams()
    const productId = productIdobj.productId

    const [errors, setErrors] = useState([])
    const [body, setBody] = useState("");
    const [rating, setRating] = useState("");

    const createBody = (e) => setBody(e.target.value)
    const createRating = (e) => setRating(e.target.value)

    // console.log(productId.productId)

    const submitHandler = (e) => {
        e.preventDefault();

       setErrors([])

       if(body.length === 0) {
        setErrors(['Review body text is required'])
      }
        const payload = {
            productId,
            body,
            rating
        }

        if (errors.length === 0) {
            dispatch(createReviewThunk(payload, sessionUser.user))
            .then(() => setBody(''))
            .catch(async response => {
                const data = await response.json()
                if(data.errors) setErrors(data.errors)
            })
        }
    }

    console.log(sessionUser.user)



    return sessionUser.user.id ? (
        <>
         <form className = "commentform" onSubmit = {submitHandler}>

<div className='newcommenterror'>
       {errors.length > 0 && errors.map((error, i) => {
               return <div key={i} >{error}</div>

           })}
</div>
           <label className='newcommenttxt' >
               New Comment:
           </label>
           <input className= "commentinput"value={body} onChange = {createBody} />
           <button className= "submitbtn">Submit</button>
       </form>
        </>
    ) :
    null;
}

export default CreateReviewForm
