import './SubNavigation.css';
import React, { useState, useEffect, useRef } from 'react';
import SearchBar from '../SearchBar/Search';
import CreateProductForm from '../ProductForm/CreateProductForm';
import OpenModalButton from '../OpenModalButton';
import { useHistory } from 'react-router-dom';
import logo from '../../../src/logo.png';
import logotext from '../../../src/logotext.png';

const SubNavigation = () => {
  const history = useHistory();
  const [displayLogotext, setDisplayLogotext] = useState(false);
  const subNavigationRef = useRef(null);

  const redirectCart = () => {
    history.push('/cart');
  };

  const handleScroll = () => {
    if (subNavigationRef.current) {
      const height = subNavigationRef.current.offsetHeight;
      const { top } = subNavigationRef.current.getBoundingClientRect();

      if (height < 40 && top >= 0) {
        setDisplayLogotext(true);
      } else {
        setDisplayLogotext(false);
      }
    }
  };

  useEffect(() => {
    window.addEventListener('scroll', handleScroll);
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <div ref={subNavigationRef} className="subNavigationContainer">
      {displayLogotext ? (
        <img src={logotext} className="logo" alt="logotext" />
      ) : (
        <img src={logo} className="logo" alt="logo" />
      )}

      <OpenModalButton modalComponent={<CreateProductForm />} buttonText="List My Product" />
      <SearchBar />
      <img
        src="https://img.uxwing.com/wp-content/themes/uxwing/download/user-interface/search-icon.png"
        alt="SearchSymbol"
        className="SearchSymbol"
      />
      <button className="cartButton" onClick={redirectCart}>
        <img
          src="https://www.freeiconspng.com/thumbs/bags-icon/bag-icon-6.png"
          alt="Shopping Cart"
          className="cartIcon"
        />
      </button>
    </div>
  );
};

export default SubNavigation;
